     */
    function tryParseAddress(string memory input) internal pure returns (bool success, address value) {
        return tryParseAddress(input, 0, bytes(input).length);
    }

    /**
     * @dev Variant of {parseAddress-string-uint256-uint256} that returns false if the parsing fails because input is not a properly
     * formatted address. See {parseAddress-string-uint256-uint256} requirements.
     */
    function tryParseAddress(
        string memory input,
        uint256 begin,
        uint256 end
    ) internal pure returns (bool success, address value) {
        if (end > bytes(input).length || begin > end) return (false, address(0));

        bool hasPrefix = (end > begin + 1) && bytes2(_unsafeReadBytesOffset(bytes(input), begin)) == bytes2("0x"); // don't do out-of-bound (possibly unsafe) read if sub-string is empty
        uint256 expectedLength = 40 + hasPrefix.toUint() * 2;

        // check that input is the correct length
        if (end - begin == expectedLength) {
            // length guarantees that this does not overflow, and value is at most type(uint160).max
            (bool s, uint256 v) = _tryParseHexUintUncheckedBounds(input, begin, end);
            return (s, address(uint160(v)));
        } else {
            return (false, address(0));
        }
    }

    function _tryParseChr(bytes1 chr) private pure returns (uint8) {
        uint8 value = uint8(chr);

        // Try to parse `chr`:
        // - Case 1: [0-9]
        // - Case 2: [a-f]
        // - Case 3: [A-F]
        // - otherwise not supported
        unchecked {
            if (value > 47 && value < 58) value -= 48;
            else if (value > 96 && value < 103) value -= 87;
            else if (value > 64 && value < 71) value -= 55;
            else return type(uint8).max;
        }

        return value;
    }

    /**
     * @dev Escape special characters in JSON strings. This can be useful to prevent JSON injection in NFT metadata.
     *
     * WARNING: This function should only be used in double quoted JSON strings. Single quotes are not escaped.
     *
     * NOTE: This function escapes all unicode characters, and not just the ones in ranges defined in section 2.5 of
     * RFC-4627 (U+0000 to U+001F, U+0022 and U+005C). ECMAScript's `JSON.parse` does recover escaped unicode
     * characters that are not in this range, but other tooling may provide different results.
     */
    function escapeJSON(string memory input) internal pure returns (string memory) {
        bytes memory buffer = bytes(input);
        bytes memory output = new bytes(2 * buffer.length); // worst case scenario
        uint256 outputLength = 0;

        for (uint256 i; i < buffer.length; ++i) {
            bytes1 char = bytes1(_unsafeReadBytesOffset(buffer, i));
            if (((SPECIAL_CHARS_LOOKUP & (1 << uint8(char))) != 0)) {
                output[outputLength++] = "\\";
                if (char == 0x08) output[outputLength++] = "b";
                else if (char == 0x09) output[outputLength++] = "t";
                else if (char == 0x0a) output[outputLength++] = "n";
                else if (char == 0x0c) output[outputLength++] = "f";
                else if (char == 0x0d) output[outputLength++] = "r";
                else if (char == 0x5c) output[outputLength++] = "\\";
                else if (char == 0x22) {
                    // solhint-disable-next-line quotes
                    output[outputLength++] = '"';
                }
            } else {
                output[outputLength++] = char;
            }
        }
        // write the actual length and deallocate unused memory
        assembly ("memory-safe") {
            mstore(output, outputLength)
            mstore(0x40, add(output, shl(5, shr(5, add(outputLength, 63)))))
        }

        return string(output);
    }

    /**
     * @dev Reads a bytes32 from a bytes array without bounds checking.
     *
     * NOTE: making this function internal would mean it could be used with memory unsafe offset, and marking the
     * assembly block as such would prevent some optimizations.
     */
    function _unsafeReadBytesOffset(bytes memory buffer, uint256 offset) private pure returns (bytes32 value) {
        // This is not memory safe in the general case, but all calls to this private function are within bounds.
        assembly ("memory-safe") {
            value := mload(add(add(buffer, 0x20), offset))
        }
    }
}

contracts/EntropyOracle.sol.content:
// SPDX-License-Identifier: BSD-3-Clause-Clear
pragma solidity ^0.8.27;

import {FHE, euint64} from "@fhevm/solidity/lib/FHE.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./interfaces/IEntropyOracle.sol";
import "./FHEChaosEngine.sol";

/**
 * @title EntropyOracle
 * @notice Main oracle contract for entropy requests - Developer-friendly interface
 * @dev Developers call requestEntropy() with 0.00001 ETH fee
 */
contract EntropyOracle is IEntropyOracle, Ownable, ReentrancyGuard {
    // ============ Constants ============

    /// @notice Fee per entropy request: 0.00001 ETH = 10000000000000 wei
    uint256 public constant FEE_AMOUNT = 0.00001 ether; // 10000000000000 wei

    // ============ State Variables ============

    /// @notice Core chaos engine
    FHEChaosEngine public chaosEngine;

    /// @notice Fee recipient address
    address public feeRecipient;

    /// @notice Request counter
    uint256 private requestCounter;

    /// @notice Request structure
    struct EntropyRequest {
        address consumer;
        bytes32 tag;
        euint64 encryptedEntropy;
        uint256 timestamp;
        bool fulfilled;
    }

    /// @notice Mapping of request ID to request
    mapping(uint256 => EntropyRequest) public requests;

    // ============ Events ============

    event EntropyRequested(
        uint256 indexed requestId,
        address indexed consumer,
        bytes32 tag,
        uint256 feePaid
    );

    event EntropyFulfilled(
        uint256 indexed requestId,
        address indexed consumer,
        bytes32 tag
    );

    event FeeRecipientUpdated(address indexed oldRecipient, address indexed newRecipient);
    event ChaosEngineUpdated(address indexed oldEngine, address indexed newEngine);

    // ============ Errors ============

    error InsufficientFee(uint256 required, uint256 provided);
    error ChaosEngineNotSet();
    error RequestNotFulfilled(uint256 requestId);
    error InvalidAddress();

    // ============ Constructor ============

    /**
     * @notice Deploy EntropyOracle
     * @param _chaosEngine Address of FHEChaosEngine contract
     * @param _feeRecipient Address to receive fees
     * @param initialOwner Initial owner address
     */
    constructor(
        address _chaosEngine,
        address _feeRecipient,
        address initialOwner
    ) Ownable(initialOwner) {
        if (_chaosEngine == address(0)) revert InvalidAddress();
        if (_feeRecipient == address(0)) revert InvalidAddress();

        chaosEngine = FHEChaosEngine(_chaosEngine);
        feeRecipient = _feeRecipient;
        requestCounter = 0;
    }

    // ============ Main Function (Developer Interface) ============

    /**
     * @notice Request entropy - Main function for developers
     * @param tag Unique tag for this request (e.g., keccak256("lottery-draw"))
     * @return requestId Unique request ID
     * @dev Requires exactly 0.00001 ETH fee
     */
    function requestEntropy(bytes32 tag) 
        external 
        payable 
        nonReentrant 
        returns (uint256 requestId) 
    {
        // Check fee
        if (msg.value < FEE_AMOUNT) {
            revert InsufficientFee(FEE_AMOUNT, msg.value);
        }

        // Increment request counter
        requestCounter++;
        requestId = requestCounter;

        // Generate entropy using chaos engine
        // Pass requestId for seed consistency
        euint64 entropy = chaosEngine.generateEntropy(tag, msg.sender, requestId);

        // Store request
        requests[requestId] = EntropyRequest({
            consumer: msg.sender,
            tag: tag,
            encryptedEntropy: entropy,
            timestamp: block.timestamp,
            fulfilled: true
        });

        // Transfer fee to recipient
        if (feeRecipient != address(0)) {
            (bool success, ) = payable(feeRecipient).call{value: msg.value}("");
            require(success, "Fee transfer failed");
        }

        emit EntropyRequested(requestId, msg.sender, tag, msg.value);
        emit EntropyFulfilled(requestId, msg.sender, tag);

        return requestId;
    }

    // ============ View Functions ============

    /**
     * @notice Get encrypted entropy for a request
     * @param requestId Request ID returned from requestEntropy
     * @return entropy Encrypted entropy (euint64)
     */
    function getEncryptedEntropy(uint256 requestId) 
        external 
        view 
        returns (euint64) 
    {
        if (!requests[requestId].fulfilled) {
            revert RequestNotFulfilled(requestId);
        }
        return requests[requestId].encryptedEntropy;
    }

    /**
     * @notice Check if request is fulfilled
     * @param requestId Request ID
     * @return fulfilled True if entropy is ready
     */
    function isRequestFulfilled(uint256 requestId) 
        external 
        view 
        returns (bool) 
    {
        return requests[requestId].fulfilled;
    }

    /**
     * @notice Get request details
     * @param requestId Request ID
     * @return consumer Consumer address
     * @return tag Request tag
     * @return timestamp Request timestamp
     * @return fulfilled Fulfillment status
     */
    function getRequest(uint256 requestId) 
        external 
        view 
        returns (
            address consumer,
            bytes32 tag,
            uint256 timestamp,
            bool fulfilled
        ) 
    {
        EntropyRequest memory request = requests[requestId];
        return (
            request.consumer,
            request.tag,
            request.timestamp,
            request.fulfilled
        );
    }

    /**
     * @notice Get current fee amount
     * @return fee Fee in wei (0.00001 ETH = 10000000000000 wei)
     */
    function getFee() external pure returns (uint256) {
        return FEE_AMOUNT;
    }

    // ============ Admin Functions ============

    /**
     * @notice Update fee recipient (owner only)
     * @param newRecipient New fee recipient address
     */
    function setFeeRecipient(address newRecipient) external onlyOwner {
        if (newRecipient == address(0)) revert InvalidAddress();

        address oldRecipient = feeRecipient;
        feeRecipient = newRecipient;

        emit FeeRecipientUpdated(oldRecipient, newRecipient);
    }

    /**
     * @notice Update chaos engine (owner only, emergency use)
     * @param newEngine New chaos engine address
     */
    function setChaosEngine(address newEngine) external onlyOwner {
        if (newEngine == address(0)) revert InvalidAddress();

        address oldEngine = address(chaosEngine);
        chaosEngine = FHEChaosEngine(newEngine);

        emit ChaosEngineUpdated(oldEngine, newEngine);
    }

    /**
     * @notice Emergency withdraw (owner only)
     * @param to Recipient address
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address to, uint256 amount) external onlyOwner {
        if (to == address(0)) revert InvalidAddress();
        (bool success, ) = payable(to).call{value: amount}("");
        require(success, "Withdraw failed");
    }
}

contracts/examples/EntropyNFT.sol.content:
// SPDX-License-Identifier: BSD-3-Clause-Clear
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import {FHE, euint64} from "@fhevm/solidity/lib/FHE.sol";
import "../interfaces/IEntropyOracle.sol";

/**
 * @title EntropyNFT
 * @notice Real ERC721 NFT contract with trait selection using EntropyOracle
 * @dev Uses encrypted entropy to randomly select traits and stores metadata on IPFS
 */
contract EntropyNFT is ERC721, ERC721URIStorage, Ownable {
    IEntropyOracle public entropyOracle;

    // NFT traits
    string[] public backgrounds = ["Red", "Blue", "Green", "Yellow", "Purple", "Orange", "Pink", "Cyan"];
    string[] public accessories = ["Hat", "Glasses", "Necklace", "Watch", "Ring", "Crown", "Mask", "None"];
    string[] public expressions = ["Happy", "Sad", "Angry", "Surprised", "Cool", "Wink", "Sleepy", "Excited"];

    // NFT structure with traits
    struct NFTData {
        uint256 tokenId;
        uint256 entropyRequestId;
        uint8 backgroundIndex;
        uint8 accessoryIndex;
        uint8 expressionIndex;
        string tokenURI;
        bool minted;
    }

    mapping(uint256 => NFTData) public nftData;
    mapping(uint256 => uint256) public requestIdToTokenId;
    uint256 public nextTokenId;
    string public baseURI;

    event NFTMintRequested(uint256 indexed tokenId, uint256 indexed requestId);
    event NFTMinted(uint256 indexed tokenId, uint256 indexed requestId, string tokenURI);

    constructor(
        address _entropyOracle,
        address initialOwner
    ) ERC721("EntropyNFT", "ENTNFT") Ownable(initialOwner) {
        require(_entropyOracle != address(0), "Invalid oracle address");
        entropyOracle = IEntropyOracle(_entropyOracle);
        nextTokenId = 1;
    }

    /**
     * @notice Set base URI for token metadata
     * @param _baseURI Base URI (e.g., "ipfs://Qm...")
     */
    function setBaseURI(string memory _baseURI) external onlyOwner {
        baseURI = _baseURI;
    }

    /**
     * @notice Request entropy for NFT trait selection
     * @param tag Unique tag for this NFT mint
     * @return tokenId The token ID for this NFT
     * @return requestId The entropy request ID
     * @dev Requires 0.00001 ETH fee for entropy request
     */
    function requestMint(bytes32 tag) external payable returns (uint256 tokenId, uint256 requestId) {
        require(msg.value >= entropyOracle.getFee(), "Insufficient fee");

        tokenId = nextTokenId++;

        // Request entropy from oracle
        requestId = entropyOracle.requestEntropy{value: msg.value}(tag);

        // Store NFT request with default values
        nftData[tokenId] = NFTData({
            tokenId: tokenId,
            entropyRequestId: requestId,
            backgroundIndex: 0,
            accessoryIndex: 0,
            expressionIndex: 0,
            tokenURI: "",
            minted: false
        });

        requestIdToTokenId[requestId] = tokenId;

        emit NFTMintRequested(tokenId, requestId);

        return (tokenId, requestId);
    }

    /**
     * @notice Complete NFT minting (traits selected using entropy)
     * @param tokenId The token ID
     * @param tokenURI IPFS URI for the NFT metadata
     * @dev Traits are selected using entropy, then NFT is minted with metadata
     */
    function completeMint(uint256 tokenId, string memory tokenURI) external {
        NFTData storage nft = nftData[tokenId];
        require(nft.tokenId == tokenId, "NFT not found");
        require(!nft.minted, "NFT already minted");
        require(entropyOracle.isRequestFulfilled(nft.entropyRequestId), "Entropy not ready");

        // Get encrypted entropy
        euint64 entropy = entropyOracle.getEncryptedEntropy(nft.entropyRequestId);

        // Note: In a real FHE implementation, you would use FHE operations to select traits
        // For now, we'll use a simplified approach where traits are selected off-chain
        // and passed via tokenURI. In production, you'd decrypt entropy or use FHE.mod operations

        // Store traits (selected off-chain based on entropy)
        // In production: Use FHE.mod(entropy, backgrounds.length) etc.
        nft.tokenURI = tokenURI;
        nft.minted = true;

        // Mint the NFT to the requester
        _safeMint(msg.sender, tokenId);
        _setTokenURI(tokenId, tokenURI);

        emit NFTMinted(tokenId, nft.entropyRequestId, tokenURI);
    }

    /**
     * @notice Complete NFT minting with trait indices (for off-chain trait selection)
     * @param tokenId The token ID
     * @param backgroundIdx Background trait index
     * @param accessoryIdx Accessory trait index
     * @param expressionIdx Expression trait index
     * @param tokenURI IPFS URI for the NFT metadata
     * @dev Traits are selected off-chain using decrypted entropy, then stored on-chain
     */
    function completeMintWithTraits(
        uint256 tokenId,
        uint8 backgroundIdx,
        uint8 accessoryIdx,
        uint8 expressionIdx,
        string memory tokenURI
    ) external {
        NFTData storage nft = nftData[tokenId];
        require(nft.tokenId == tokenId, "NFT not found");
        require(!nft.minted, "NFT already minted");
        require(entropyOracle.isRequestFulfilled(nft.entropyRequestId), "Entropy not ready");
        require(backgroundIdx < backgrounds.length, "Invalid background index");
        require(accessoryIdx < accessories.length, "Invalid accessory index");
        require(expressionIdx < expressions.length, "Invalid expression index");

        // Store traits
        nft.backgroundIndex = backgroundIdx;
        nft.accessoryIndex = accessoryIdx;
        nft.expressionIndex = expressionIdx;
        nft.tokenURI = tokenURI;
        nft.minted = true;

        // Mint the NFT to the requester
        _safeMint(msg.sender, tokenId);
        _setTokenURI(tokenId, tokenURI);

        emit NFTMinted(tokenId, nft.entropyRequestId, tokenURI);
    }

    /**
     * @notice Get NFT information with traits
     * @param tokenId The token ID
     * @return tokenId_ The token ID
     * @return entropyRequestId The entropy request ID
     * @return background The background trait
     * @return accessory The accessory trait
     * @return expression The expression trait
     * @return tokenURI The token URI
     * @return minted Whether NFT is minted
     */
    function getNFT(uint256 tokenId) external view returns (
        uint256 tokenId_,
        uint256 entropyRequestId,
        string memory background,
        string memory accessory,
        string memory expression,
        string memory tokenURI,
        bool minted
    ) {
        NFTData memory nft = nftData[tokenId];
        return (
            nft.tokenId,
            nft.entropyRequestId,
            backgrounds[nft.backgroundIndex],
            accessories[nft.accessoryIndex],
            expressions[nft.expressionIndex],
            nft.tokenURI,
            nft.minted
        );
    }

    /**
     * @notice Get available traits
     * @return backgrounds_ Available background colors
     * @return accessories_ Available accessories
     * @return expressions_ Available expressions
     */
    function getAvailableTraits() external view returns (
        string[] memory backgrounds_,
        string[] memory accessories_,
        string[] memory expressions_
    ) {
        return (backgrounds, accessories, expressions);
    }

    /**
     * @notice Get total supply (minted NFTs)
     * @return count Total minted NFTs
     */
    function totalSupply() external view returns (uint256 count) {
        return nextTokenId - 1;
    }

    // Override required by Solidity
    function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721URIStorage) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

contracts/FHEChaosEngine.sol.content:
// SPDX-License-Identifier: BSD-3-Clause-Clear
pragma solidity ^0.8.27;

import {FHE, euint64, externalEuint64} from "@fhevm/solidity/lib/FHE.sol";
import {ZamaEthereumConfig} from "@fhevm/solidity/config/ZamaConfig.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./libraries/SeedCollector.sol";
import "./libraries/LogisticMap.sol";

/**
 * @title FHEChaosEngine
 * @notice Core FHE-based chaos engine for entropy generation
 * @dev Uses logistic map with internal seeds only (no external seeds)
 */
contract FHEChaosEngine is ZamaEthereumConfig, Ownable {
    using SeedCollector for euint64;
    using LogisticMap for euint64;

    // Master seed (encrypted, set by owner)
    euint64 private masterSeed;

    // Current state (encrypted, updated with each iteration)
    euint64 private currentState;

    // Request counter (for unique seed generation)
    uint256 private requestCounter;

    // Master seed initialized flag
    bool private masterSeedInitialized;

    // ============ Events ============

    event MasterSeedInitialized(address indexed owner);
    event EntropyGenerated(uint256 indexed requestId, address indexed consumer);

    // ============ Errors ============

    error MasterSeedNotInitialized();
    error InvalidMasterSeed();

    // ============ Constructor ============

    constructor(address initialOwner) Ownable(initialOwner) {
        masterSeedInitialized = false;
        requestCounter = 0;
    }

    // ============ Master Seed Management ============

    /**
     * @notice Initialize master seed (owner only, one-time)
     * @param encryptedSeed Encrypted master seed (euint64)
     * @param inputProof Input proof for encrypted seed
     * @dev Master seed is used in every entropy generation
     */
    function initializeMasterSeed(
        externalEuint64 encryptedSeed,
        bytes calldata inputProof
    ) external onlyOwner {
        require(!masterSeedInitialized, "Master seed already initialized");

        // Convert external encrypted seed to internal
        euint64 internalSeed = FHE.fromExternal(encryptedSeed, inputProof);

        // Allow contract to use encrypted value (like ShadeFX does)
        FHE.allowThis(internalSeed);
        FHE.allow(internalSeed, msg.sender);

        // Make decryptable for FHE operations (ShadeFX pattern)
        masterSeed = FHE.makePubliclyDecryptable(internalSeed);

        // Initialize current state with master seed
        currentState = masterSeed;

        masterSeedInitialized = true;

        emit MasterSeedInitialized(owner());
    }

    // ============ Entropy Generation ============

    /**
     * @notice Generate entropy using chaos function
     * @param tag Request tag for uniqueness
     * @param consumer Consumer address
     * @param requestId Request ID from EntropyOracle (for seed consistency)
     * @return entropy Encrypted entropy (euint64)
     * @dev Uses internal seeds only: master seed + blockchain data + request data
     */
    function generateEntropy(
        bytes32 tag,
        address consumer,
        uint256 requestId
    ) external returns (euint64 entropy) {
        if (!masterSeedInitialized) revert MasterSeedNotInitialized();

        // Collect internal seeds (NO external seeds)
        // Use requestId from EntropyOracle for consistency
        euint64 aggregatedSeed = SeedCollector.collectInternalSeeds(
            masterSeed,
            requestId,
            consumer,
            tag
        );

        // Combine with current state
        euint64 combinedSeed = FHE.xor(currentState, aggregatedSeed);

        // Iterate chaos function (logistic map)
        // Single iteration to minimize FHE operations
        euint64 newState = LogisticMap.iterate(combinedSeed);

        // Allow contract to use new state in next iteration (instead of makePubliclyDecryptable)
        // This allows FHE operations without making it publicly decryptable
        FHE.allowThis(newState);

        // Update current state
        currentState = newState;

        // Return entropy (make decryptable only for return value, not for storage)
        entropy = FHE.makePubliclyDecryptable(newState);

        emit EntropyGenerated(requestId, consumer);

        return entropy;
    }

    // ============ View Functions ============

    /**
     * @notice Check if master seed is initialized
     * @return initialized True if master seed is set
     */
    function isMasterSeedInitialized() external view returns (bool initialized) {
        return masterSeedInitialized;
    }

    /**
     * @notice Get request counter
     * @return counter Current request counter
     */
    function getRequestCounter() external view returns (uint256 counter) {
        return requestCounter;
    }
}

contracts/interfaces/IEntropyOracle.sol.content:
// SPDX-License-Identifier: BSD-3-Clause-Clear
pragma solidity ^0.8.27;

import {euint64} from "@fhevm/solidity/lib/FHE.sol";

/**
 * @title IEntropyOracle
 * @notice Interface for Entropy Oracle - Developer-friendly entropy source
 * @dev Developers integrate this interface to request encrypted entropy
 */
interface IEntropyOracle {
    /**
     * @notice Request entropy (main function for developers)
     * @param tag Unique tag for this request (e.g., keccak256("lottery-draw"))
     * @return requestId Unique request ID
     * @dev Requires 0.00001 ETH fee
     */
    function requestEntropy(bytes32 tag) external payable returns (uint256 requestId);

    /**
     * @notice Get encrypted entropy for a request
     * @param requestId Request ID returned from requestEntropy
     * @return entropy Encrypted entropy (euint64)
     * @dev Returns encrypted value - use in FHE operations
     */
    function getEncryptedEntropy(uint256 requestId) external view returns (euint64);

    /**
     * @notice Check if request is fulfilled
     * @param requestId Request ID
     * @return fulfilled True if entropy is ready
     */
    function isRequestFulfilled(uint256 requestId) external view returns (bool);

    /**
     * @notice Get request details
     * @param requestId Request ID
     * @return consumer Consumer address
     * @return tag Request tag
     * @return timestamp Request timestamp
     * @return fulfilled Fulfillment status
     */
    function getRequest(uint256 requestId) external view returns (
        address consumer,
        bytes32 tag,
        uint256 timestamp,
        bool fulfilled
    );

    /**
     * @notice Get current fee amount
     * @return fee Fee in wei (0.00001 ETH = 10000000000000 wei)
     */
    function getFee() external pure returns (uint256);
}

contracts/libraries/LogisticMap.sol.content:
// SPDX-License-Identifier: BSD-3-Clause-Clear
pragma solidity ^0.8.27;

import {FHE, euint64} from "@fhevm/solidity/lib/FHE.sol";

/**
 * @title LogisticMap
 * @notice Library for logistic map chaos function in FHE
 * @dev x[n+1] = r * x[n] * (1 - x[n])
 *      Where r is control parameter (typically 3.57-4.0)
 *      x is state (0.0-1.0 range, scaled to uint64)
 */
library LogisticMap {
    // Control parameter r (scaled by 1e18 for precision)
    // r = 3.9 (good chaos parameter)
    uint256 private constant R_SCALED = 3900000000000000000; // 3.9 * 1e18

    // Maximum value for uint64
    uint256 private constant MAX_UINT64 = type(uint64).max;

    /**
     * @notice Iterate chaos function once (FHE-friendly version)
     * @param x Current state (encrypted, scaled 0-MAX_UINT64)
     * @return newX Next state (encrypted)
     * @dev Simplified chaos function for FHE: x[n+1] = x[n] * (MAX_UINT64 - x[n]) * 4
     *      Uses XOR for additional mixing to maintain chaos properties
     *      Overflow is allowed (uint64 wraps naturally)
     */
    function iterate(euint64 x) internal returns (euint64 newX) {
        // Logistic map-inspired chaos function for FHE
        // Minimal operations: 2 FHE ops per iteration

        // Multiply by prime for non-linear transformation
        euint64 xScaled = FHE.mul(x, FHE.asEuint64(17));

        // XOR with original for mixing (creates chaos)
        newX = FHE.xor(xScaled, x);

        return newX;
    }

    /**
     * @notice Iterate logistic map multiple times
     * @param x Initial state (encrypted)
     * @param iterations Number of iterations
     * @return finalX Final state after iterations (encrypted)
     */
    function iterateMultiple(euint64 x, uint256 iterations) internal returns (euint64 finalX) {
        finalX = x;
        for (uint256 i = 0; i < iterations; i++) {
            finalX = iterate(finalX);
        }
        return finalX;
    }
}

